    }
}

contracts/SKB/closedsea/OperatorFilterer.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// @notice Optimized and flexible operator filterer to abide to OpenSea's
/// mandatory on-chain royalty enforcement in order for new collections to
/// receive royalties.
/// For more information, see:
/// See: https://github.com/ProjectOpenSea/operator-filter-registry
abstract contract OperatorFilterer {
    /// @dev The default OpenSea operator blocklist subscription.
    address internal constant _DEFAULT_SUBSCRIPTION = 0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6;

    /// @dev The OpenSea operator filter registry.
    address internal constant _OPERATOR_FILTER_REGISTRY = 0x000000000000AAeB6D7670E522A718067333cd4E;

    /// @dev Registers the current contract to OpenSea's operator filter,
    /// and subscribe to the default OpenSea operator blocklist.
    /// Note: Will not revert nor update existing settings for repeated registration.
    function _registerForOperatorFiltering() internal virtual {
        _registerForOperatorFiltering(_DEFAULT_SUBSCRIPTION, true);
    }

    /// @dev Registers the current contract to OpenSea's operator filter.
    /// Note: Will not revert nor update existing settings for repeated registration.
    function _registerForOperatorFiltering(address subscriptionOrRegistrantToCopy, bool subscribe)
        internal
        virtual
    {
        /// @solidity memory-safe-assembly
        assembly {
            let functionSelector := 0x7d3e3dbe // `registerAndSubscribe(address,address)`.

            // Clean the upper 96 bits of `subscriptionOrRegistrantToCopy` in case they are dirty.
            subscriptionOrRegistrantToCopy := shr(96, shl(96, subscriptionOrRegistrantToCopy))

            for {} iszero(subscribe) {} {
                if iszero(subscriptionOrRegistrantToCopy) {
                    functionSelector := 0x4420e486 // `register(address)`.
                    break
                }
                functionSelector := 0xa0af2903 // `registerAndCopyEntries(address,address)`.
                break
            }
            // Store the function selector.
            mstore(0x00, shl(224, functionSelector))
            // Store the `address(this)`.
            mstore(0x04, address())
            // Store the `subscriptionOrRegistrantToCopy`.
            mstore(0x24, subscriptionOrRegistrantToCopy)
            // Register into the registry.
            if iszero(call(gas(), _OPERATOR_FILTER_REGISTRY, 0, 0x00, 0x44, 0x00, 0x04)) {
                // If the function selector has not been overwritten,
                // it is an out-of-gas error.
                if eq(shr(224, mload(0x00)), functionSelector) {
                    // To prevent gas under-estimation.
                    revert(0, 0)
                }
            }
            // Restore the part of the free memory pointer that was overwritten,
            // which is guaranteed to be zero, because of Solidity's memory size limits.
            mstore(0x24, 0)
        }
    }

    /// @dev Modifier to guard a function and revert if the caller is a blocked operator.
    modifier onlyAllowedOperator(address from) virtual {
        if (from != msg.sender) {
            if (!_isPriorityOperator(msg.sender)) {
                if (_operatorFilteringEnabled()) _revertIfBlocked(msg.sender);
            }
        }
        _;
    }

    /// @dev Modifier to guard a function from approving a blocked operator..
    modifier onlyAllowedOperatorApproval(address operator) virtual {
        if (!_isPriorityOperator(operator)) {
            if (_operatorFilteringEnabled()) _revertIfBlocked(operator);
        }
        _;
    }

    /// @dev Helper function that reverts if the `operator` is blocked by the registry.
    function _revertIfBlocked(address operator) private view {
        /// @solidity memory-safe-assembly
        assembly {
            // Store the function selector of `isOperatorAllowed(address,address)`,
            // shifted left by 6 bytes, which is enough for 8tb of memory.
            // We waste 6-3 = 3 bytes to save on 6 runtime gas (PUSH1 0x224 SHL).
            mstore(0x00, 0xc6171134001122334455)
            // Store the `address(this)`.
            mstore(0x1a, address())
            // Store the `operator`.
            mstore(0x3a, operator)

            // `isOperatorAllowed` always returns true if it does not revert.
            if iszero(staticcall(gas(), _OPERATOR_FILTER_REGISTRY, 0x16, 0x44, 0x00, 0x00)) {
                // Bubble up the revert if the staticcall reverts.
                returndatacopy(0x00, 0x00, returndatasize())
                revert(0x00, returndatasize())
            }

            // We'll skip checking if `from` is inside the blacklist.
            // Even though that can block transferring out of wrapper contracts,
            // we don't want tokens to be stuck.

            // Restore the part of the free memory pointer that was overwritten,
            // which is guaranteed to be zero, if less than 8tb of memory is used.
            mstore(0x3a, 0)
        }
    }

    /// @dev For deriving contracts to override, so that operator filtering
    /// can be turned on / off.
    /// Returns true by default.
    function _operatorFilteringEnabled() internal view virtual returns (bool) {
        return true;
    }

    /// @dev For deriving contracts to override, so that preferred marketplaces can
    /// skip operator filtering, helping users save gas.
    /// Returns false for all inputs by default.
    function _isPriorityOperator(address) internal view virtual returns (bool) {
        return false;
    }
}

contracts/SKB/MultisigOwnable.sol.content:
// SPDX-License-Identifier: CC0-1.0
// Source: https://github.com/tubby-cats/dual-ownership-nft
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/access/Ownable.sol';

abstract contract MultisigOwnable is Ownable {
  address public realOwner;

  constructor() {
    realOwner = msg.sender;
  }

  modifier onlyRealOwner() {
    require(
      realOwner == msg.sender,
      'MultisigOwnable: caller is not the real owner'
    );
    _;
  }

  function transferRealOwnership(address newRealOwner) public onlyRealOwner {
    realOwner = newRealOwner;
  }

  function transferLowerOwnership(address newOwner) public onlyRealOwner {
    transferOwnership(newOwner);
  }
}

contracts/SKB/SpheraKitBag.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "erc721a/contracts/ERC721A.sol";

import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {BitMaps} from "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import "./closedsea/OperatorFilterer.sol";
import "./MultisigOwnable.sol";


error InvalidPeriodSetup();
error MaxPeriodMintSupplyReached();
error RedeemBagNotOpen();
error MismatchedArrays();
error BagRedeemerNotSet();
error ForceRedeemBagOwnerMismatch();
error RegistryNotSet();
error NotAllowedByRegistry();
error WithdrawFailed();
error ClaimWindowNotOpen();
error MismatchedTokenOwnerForClaim();
error BagCannotBeClaimed();
error InitialTransferLockOn();
error MaxAuctionMintForAddress();
error InsufficientFunds();
error RefundFailed();
error OverMaxSupply();
error AllowlistMintNotOpen();
error PeriodNotOpen();
error MintingTooMuchInPeriod();
error InvalidContractSetup();

interface IBagRedeemer {
    function redeemBags(address to, uint256[] calldata BagIds)
        external
        returns (uint256[] memory);
}

interface IRegistry {
    function isAllowedOperator(address operator) external view returns (bool);
}

contract SpheraKitBag is ERC2981, MultisigOwnable, OperatorFilterer, ERC721A {
    using ECDSA for bytes32;
    using EnumerableSet for EnumerableSet.UintSet;

    event AirdroppedChunk(uint256 indexed chunkNum);
    event ClaimedBag(uint256 indexed sourceAzukiId, uint256 indexed BagId);
    event PeriodMint(address indexed minter, uint256 period, uint16 indexed amount);

    // The set of chunks processed for the airdrop.
    // Intent is to help prevent double processing of chunks.
    EnumerableSet.UintSet private _processedChunksForAirdrop;

    bool public operatorFilteringEnabled = true;
    bool public initialTransferLockOn = true;
    bool public isRegistryActive = false;
    address public registryAddress;


    uint256 public constant GTD_PERIOD = 1;
    uint256 public constant FSFS_PERIOD = 2;
    uint256 public constant PUBLIC_PERIOD = 3;
    struct PeriodInfo {
        uint32 startTime;
        uint32 endTime;
        uint64 price;
        uint64 MAX_MINT_ALLOWED;
        uint64 MAX_SUPPLY;
    }
    mapping(uint256 => PeriodInfo) public periodInfo;
    mapping(uint256 => mapping(address => uint256)) public individualMintedInPeriod;
    
    mapping(uint256 => uint64) public totalMintedInPeriod;
    uint256 public period;

    struct RedeemInfo {
        bool redeemBagOpen;
        address BagRedeemer;
    }
    RedeemInfo public redeemInfo;

    mapping(address => uint256) public allowlistMintsAlloc;
    uint256 public allowlistMintPrice;

    uint256 public immutable MAX_SUPPLY;

    string private _baseTokenURI;

    address payable public immutable WITHDRAW_ADDRESS;

    uint256 public constant MINT_BATCH_SIZE = 10;

    constructor(
        uint256 _maxSupply,
        address payable _withdrawAddress
    ) ERC721A("SpheraKitBag", "SKB") {
        MAX_SUPPLY = _maxSupply;
        WITHDRAW_ADDRESS = _withdrawAddress;
    }
    // ------------
    // Period mint
    // ------------
    // maxAllowedForPeriodForAddr: the number the holder is allowed to mint during the entirety of the period.
    function periodMint(
        uint256 _period, 
        uint16 amount
    ) external payable {
        PeriodInfo memory info = periodInfo[_period];
        if (
            period != _period ||
            info.startTime == 0 ||
            block.timestamp < info.startTime ||
            block.timestamp >= info.endTime
        ) {
            revert PeriodNotOpen();
        }

        uint256 numMintedInPeriodLoc = individualMintedInPeriod[_period][msg.sender];
        if (amount > info.MAX_MINT_ALLOWED - individualMintedInPeriod[_period][msg.sender]) {
            revert MintingTooMuchInPeriod();
        }

        uint64 totalPeriodMintedLocal = totalMintedInPeriod[_period];
        if (amount + totalPeriodMintedLocal > info.MAX_SUPPLY) {
            revert MaxPeriodMintSupplyReached();
        }

        if (_totalMinted() + amount > MAX_SUPPLY) {
            revert OverMaxSupply();
        }

        uint256 totalCost = uint256(info.price) * amount;
        if (msg.value < totalCost) {
            revert InsufficientFunds();
        }
        unchecked {
            individualMintedInPeriod[_period][msg.sender] = amount + numMintedInPeriodLoc;
            totalMintedInPeriod[_period] = totalPeriodMintedLocal + amount;
        }
        _mint(msg.sender, amount);
        emit PeriodMint(msg.sender, _period, amount);
    }

    function setPeriod(uint256 _period) public onlyOwner{
        period = _period;
    }

    // Period price to match starting price of dutch auction
    function setPeriodParams(
        uint256 _period,
        uint32 _startTime,
        uint32 _endTime,
        uint64 _price,
        uint64 _MAX_MINT_ALLOWED,
        uint64 _MAX_SUPPLY
    ) external onlyOwner {
        if (
            _startTime == 0 || _endTime == 0 || _price == 0
        ) {
            revert InvalidPeriodSetup();
        }
        if (_startTime >= _endTime) {
            revert InvalidPeriodSetup();
        }
        periodInfo[_period] = PeriodInfo(
            _startTime,
            _endTime,
            _price,
            _MAX_MINT_ALLOWED,
            _MAX_SUPPLY
        );
    }

    function withdraw() external {
        (bool sent, ) = WITHDRAW_ADDRESS.call{value: address(this).balance}("");
        if (!sent) {
            revert WithdrawFailed();
        }
    }

    // -----------
    // Redeem Bag
    // -----------
    function redeemBags(uint256[] calldata BagIds)
        external
        returns (uint256[] memory)
    {
        RedeemInfo memory info = redeemInfo;
        if (!info.redeemBagOpen) {
            revert RedeemBagNotOpen();
        }
        return _redeemBagsImpl(msg.sender, BagIds, true, info.BagRedeemer);
    }

    function _redeemBagsImpl(
        address BagOwner,
        uint256[] memory BagIds,
        bool burnOwnerOrApprovedCheck,
        address BagRedeemer
    ) private returns (uint256[] memory) {
        for (uint256 i; i < BagIds.length; ) {
            _burn(BagIds[i], burnOwnerOrApprovedCheck);
            unchecked {
                ++i;
            }
        }
        return IBagRedeemer(BagRedeemer).redeemBags(BagOwner, BagIds);
    }

    function forceRedeemBags(address BagOwner, uint256[] calldata BagIds)
        external
        onlyOwner
        returns (uint256[] memory)
    {
        for (uint256 i; i < BagIds.length; ) {
            if (ownerOf(BagIds[i]) != BagOwner) {
                revert ForceRedeemBagOwnerMismatch();
            }
            unchecked {
                ++i;
            }
        }
        return
            _redeemBagsImpl(
                BagOwner,
                BagIds,
                false,
                redeemInfo.BagRedeemer
            );
    }

    function openRedeemBagState() external onlyOwner {
        RedeemInfo memory info = redeemInfo;
        if (info.BagRedeemer == address(0)) {
            revert BagRedeemerNotSet();
        }
        redeemInfo = RedeemInfo(true, info.BagRedeemer);
    }

    function setBagRedeemer(address contractAddress) external onlyOwner {
        redeemInfo = RedeemInfo(redeemInfo.redeemBagOpen, contractAddress);
    }

    // --------------
    // Allowlist mint
    // --------------
    function allowlistMint() external payable {
        if (allowlistMintPrice == 0) {
            revert AllowlistMintNotOpen();
        }
        uint256 amount = allowlistMintsAlloc[msg.sender];

        uint256 totalCost = allowlistMintPrice * amount;
        if (msg.value < totalCost) {
            revert InsufficientFunds();
        }
        if (_totalMinted() + amount > MAX_SUPPLY) {
            revert OverMaxSupply();
        }
        allowlistMintsAlloc[msg.sender] = 0;

        _safeMint(msg.sender, amount);
    }

    function setAllowlistMintsAlloc(
        address[] calldata addresses,
        uint256[] calldata amounts
    ) external onlyOwner {
        if (addresses.length != amounts.length || addresses.length == 0)
            revert MismatchedArrays();
        for (uint256 i; i < addresses.length; ) {
            allowlistMintsAlloc[addresses[i]] = amounts[i];
            unchecked {
                ++i;
            }
        }
    }

    function setAllowlistMintPrice(uint256 price) external onlyOwner {
        allowlistMintPrice = price;
    }

    // -------------------
    // Break transfer lock
    // -------------------
    function breakTransferLock() external onlyOwner {
        initialTransferLockOn = false;
    }

    // --------
    // Metadata
    // --------

    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }

    function setBaseURI(string calldata baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    // --------
    // EIP-2981
    // --------
    function setDefaultRoyalty(address receiver, uint96 feeNumerator)
        external
        onlyOwner
    {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    function setTokenRoyalty(
        uint256 tokenId,
        address receiver,
        uint96 feeNumerator
    ) external onlyOwner {
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    // ---------------------------------------------------
    // OperatorFilterer overrides (overrides, values etc.)
    // ---------------------------------------------------
    function setApprovalForAll(address operator, bool approved)
        public
        override(ERC721A)
        onlyAllowedOperatorApproval(operator)
    {
        if (initialTransferLockOn) revert InitialTransferLockOn();
        super.setApprovalForAll(operator, approved);
    }

    function setOperatorFilteringEnabled(bool value) public onlyOwner {
        operatorFilteringEnabled = value;
    }

    function _operatorFilteringEnabled() internal view override returns (bool) {
        return operatorFilteringEnabled;
    }

    function approve(address operator, uint256 tokenId)
        public
        payable
        override(ERC721A)
        onlyAllowedOperatorApproval(operator)
    {
        if (initialTransferLockOn) revert InitialTransferLockOn();
        super.approve(operator, tokenId);
    }

    // ERC721A calls transferFrom internally in its two safeTransferFrom functions, so we don't need to override those.
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override(ERC721A) onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    // --------------
    // Registry check
    // --------------
    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal override {
        if (initialTransferLockOn && from != address(0) && to != address(0))
            revert InitialTransferLockOn();
        if (_isValidAgainstRegistry(msg.sender)) {
            super._beforeTokenTransfers(from, to, startTokenId, quantity);
        } else {
            revert NotAllowedByRegistry();
        }
    }

    function _isValidAgainstRegistry(address operator)
        internal
        view
        returns (bool)
    {
        if (isRegistryActive) {
            IRegistry registry = IRegistry(registryAddress);
            return registry.isAllowedOperator(operator);
        }
        return true;
    }

    function setIsRegistryActive(bool _isRegistryActive) external onlyOwner {
        if (registryAddress == address(0)) revert RegistryNotSet();
        isRegistryActive = _isRegistryActive;
    }

    function setRegistryAddress(address _registryAddress) external onlyOwner {
        registryAddress = _registryAddress;
    }
    // ----------------------------------------------
    // EIP-165
    // ----------------------------------------------
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721A, ERC2981)
        returns (bool)
    {
        return
            ERC721A.supportsInterface(interfaceId) ||
            ERC2981.supportsInterface(interfaceId);
    }
}

